mapper.select:
SELECT ifnull(count(*), 0)
FROM comment
WHERE bbs_seq=#{seq}

mapper.update:
UPDATE comment
SET del = 1
WHERE seq = #{seq}

mapper.select:
SELECT seq, id, content, bbs_seq bbsSeq, created_at createdAt, del
FROM comment
WHERE seq=#{seq}

mapper.update:
UPDATE comment
SET content = #{content}
WHERE seq = #{seq}
